#include "temperature_sensor_cpi_thermostat.h"

// Do not edit this file as it will be overwritten if codegen is rerun

void temperature_sensor_cpi_thermostat_initialize(void);
void temperature_sensor_cpi_thermostat_notify(microkit_channel channel);
void temperature_sensor_cpi_thermostat_timeTriggered(void);

volatile sb_queue_Isolette_Data_Model_TempWstatus_i_1_t *current_tempWstatus_queue_1;
volatile sb_queue_Isolette_Data_Model_PhysicalTemp_i_1_t *air_queue_1;
sb_queue_Isolette_Data_Model_PhysicalTemp_i_1_Recv_t air_recv_queue;

#define PORT_FROM_MON 42

bool put_current_tempWstatus(const Isolette_Data_Model_TempWstatus_i *data) {
  sb_queue_Isolette_Data_Model_TempWstatus_i_1_enqueue((sb_queue_Isolette_Data_Model_TempWstatus_i_1_t *) current_tempWstatus_queue_1, (Isolette_Data_Model_TempWstatus_i *) data);

  return true;
}

Isolette_Data_Model_PhysicalTemp_i last_air_payload;

bool get_air(Isolette_Data_Model_PhysicalTemp_i *data) {
  sb_event_counter_t numDropped;
  Isolette_Data_Model_PhysicalTemp_i fresh_data;
  bool isFresh = sb_queue_Isolette_Data_Model_PhysicalTemp_i_1_dequeue((sb_queue_Isolette_Data_Model_PhysicalTemp_i_1_Recv_t *) &air_recv_queue, &numDropped, &fresh_data);
  if (isFresh) {
    last_air_payload = fresh_data;
  }
  *data = last_air_payload;
  return isFresh;
}

void init(void) {
  sb_queue_Isolette_Data_Model_TempWstatus_i_1_init((sb_queue_Isolette_Data_Model_TempWstatus_i_1_t *) current_tempWstatus_queue_1);

  sb_queue_Isolette_Data_Model_PhysicalTemp_i_1_Recv_init(&air_recv_queue, (sb_queue_Isolette_Data_Model_PhysicalTemp_i_1_t *) air_queue_1);

  temperature_sensor_cpi_thermostat_initialize();
}

void notified(microkit_channel channel) {
  switch (channel) {
    case PORT_FROM_MON:
      temperature_sensor_cpi_thermostat_timeTriggered();
      break;
    default:
      temperature_sensor_cpi_thermostat_notify(channel);
  }
}
