static mut app: Option<r2u2_monitor_r2u2_r2u2> = None;
static mut init_api: r2u2_monitor_r2u2_r2u2_Application_Api<r2u2_monitor_r2u2_r2u2_Initialization_Api> = api::init_api();
static mut compute_api: r2u2_monitor_r2u2_r2u2_Application_Api<r2u2_monitor_r2u2_r2u2_Compute_Api> = api::compute_api();

#[no_mangle]
pub extern "C" fn r2u2_monitor_r2u2_r2u2_initialize() {
  #[cfg(not(test))]
  #[cfg(feature = "sel4")]
  logging::LOGGER.set().unwrap();

  unsafe {
    let mut _app = r2u2_monitor_r2u2_r2u2::new();
    _app.initialize(&mut init_api);
    app = Some(_app);
  }
}

#[no_mangle]
pub extern "C" fn r2u2_monitor_r2u2_r2u2_timeTriggered() {
  unsafe {
    if let Some(_app) = app.as_mut() {
      _app.timeTriggered(&mut compute_api);
    } else {
      panic!("Unexpected: app is None");
    }
  }
}

#[no_mangle]
pub extern "C" fn r2u2_monitor_r2u2_r2u2_notify(channel: microkit_channel) {
  unsafe {
    if let Some(_app) = app.as_mut() {
      _app.notify(channel);
    } else {
      panic!("Unexpected: app is None");
    }
  }
}

// Need a Panic handler in a no_std environment
#[panic_handler]
#[cfg(feature = "sel4")]
#[cfg(not(test))]
fn panic(info: &core::panic::PanicInfo) -> ! {
  error!("PANIC: {info:#?}");
  loop {}
}

