use data::*;

use proptest::prelude::*;

use super::test_apis::*;

use crate::bridge::consumer_consumer_GUMBOX as GUMBOX;

pub enum HarnessResult {
  RejectedPrecondition,
  FailedPostcondition(TestCaseError),
  Passed,
}

/** Contract-based test harness for the initialize entry point
  */
pub fn testInitializeCB() -> HarnessResult
{
  // [InvokeEntryPoint]: Invoke the entry point
  crate::consumer_consumer_initialize();

  return HarnessResult::Passed
}

#[macro_export]
macro_rules!
testInitializeCB_macro {
  (
    $test_name: ident,
    config: $config:expr
  ) => {
    proptest!{
      #![proptest_config($config)]
      #[test]
      #[serial]
      fn $test_name(empty in ::proptest::strategy::Just(())) {
        match $crate::test::util::cb_apis::testInitializeCB() {
          $crate::test::util::cb_apis::HarnessResult::RejectedPrecondition => {
            unreachable!("This branch is infeasible")
          }
          $crate::test::util::cb_apis::HarnessResult::FailedPostcondition(e) => {
            return Err(e)
          }
          $crate::test::util::cb_apis::HarnessResult::Passed => { }
        }
      }
    }
  };
}

/** Contract-based test harness for the compute entry point
  *
  * @param api_MyArrayInt32 incoming event data port
  * @param api_MyArrayStruct incoming event data port
  * @param api_myStructArray incoming event data port
  */
pub fn testComputeCB(
  api_MyArrayInt32: Option<Gumbo_Structs_Arrays::MyArrayInt32>,
  api_MyArrayStruct: Option<Gumbo_Structs_Arrays::MyArrayStruct>,
  api_myStructArray: Option<Gumbo_Structs_Arrays::MyStructArray_i>) -> HarnessResult
{
  // Initialize the app
  crate::consumer_consumer_initialize();

  // [CheckPre]: check/filter based on pre-condition.
  if !GUMBOX::compute_CEP_Pre (api_MyArrayInt32, api_MyArrayStruct, api_myStructArray) {
    return HarnessResult::RejectedPrecondition;
  }

  // [PutInPorts]: Set values on the input ports
  put_MyArrayInt32(api_MyArrayInt32);
  put_MyArrayStruct(api_MyArrayStruct);
  put_myStructArray(api_myStructArray);

  // [InvokeEntryPoint]: Invoke the entry point
  crate::consumer_consumer_timeTriggered();

  // [RetrieveOutState]: retrieve values of the output ports via get operations and GUMBO declared local state variable

  // [CheckPost]: invoke the oracle function
  if !GUMBOX::compute_CEP_Post(api_MyArrayInt32, api_MyArrayStruct, api_myStructArray) {
    return HarnessResult::FailedPostcondition(TestCaseError::Fail("Postcondition failed: incorrect output behavior".into()));
  }

  return HarnessResult::Passed
}

/** Contract-based test harness for the compute entry point
  */
pub fn testComputeCB_container(container: PreStateContainer) -> HarnessResult
{
  return testComputeCB(container.api_MyArrayInt32, container.api_MyArrayStruct, container.api_myStructArray)
}

#[macro_export]
macro_rules!
testComputeCB_macro {
  (
    $test_name: ident,
    config: $config:expr,
    api_MyArrayInt32: $api_MyArrayInt32_strat:expr,
    api_MyArrayStruct: $api_MyArrayStruct_strat:expr,
    api_myStructArray: $api_myStructArray_strat:expr
  ) => {
    proptest!{
      #![proptest_config($config)]
      #[test]
      #[serial]
      fn $test_name(
        (api_MyArrayInt32, api_MyArrayStruct, api_myStructArray)
            in ($api_MyArrayInt32_strat, $api_MyArrayStruct_strat, $api_myStructArray_strat)
      ) {
        match$crate::test::util::cb_apis::testComputeCB(api_MyArrayInt32, api_MyArrayStruct, api_myStructArray) {
          $crate::test::util::cb_apis::HarnessResult::RejectedPrecondition => {
            return Err(proptest::test_runner::TestCaseError::reject(
              "Precondition failed: invalid input combination",
            ))
          }
          $crate::test::util::cb_apis::HarnessResult::FailedPostcondition(e) => {
            return Err(e)
          }
          $crate::test::util::cb_apis::HarnessResult::Passed => { }
        }
      }
    }
  };
}
