// Do not edit this file as it will be overwritten if codegen is rerun

mod bridge;
mod component;
mod logging;
mod tests;

use crate::bridge::producer_producer_api::{self as api, *};
use crate::component::producer_producer_app::*;
use data::*;

static mut app: Option<producer_producer> = None;
static mut init_api: producer_producer_Application_Api<producer_producer_Initialization_Api> = api::init_api();
static mut compute_api: producer_producer_Application_Api<producer_producer_Compute_Api> = api::compute_api();

#[no_mangle]
pub extern "C" fn producer_producer_initialize() {
  logging::init_logging();

  unsafe {
    #[cfg(test)]
    crate::bridge::extern_c_api::initialize_test_globals();

    let mut _app = producer_producer::new();
    _app.initialize(&mut init_api);
    app = Some(_app);
  }
}

#[no_mangle]
pub extern "C" fn producer_producer_timeTriggered() {
  unsafe {
    if let Some(_app) = app.as_mut() {
      _app.timeTriggered(&mut compute_api);
    } else {
      panic!("Unexpected: app is None");
    }
  }
}

#[no_mangle]
pub extern "C" fn producer_producer_notify(channel: microkit_channel) {
  unsafe {
    if let Some(_app) = app.as_mut() {
      _app.notify(channel);
    } else {
      panic!("Unexpected: app is None");
    }
  }
}

// Need a Panic handler in a no_std environment
#[panic_handler]
#[cfg(not(test))]
fn panic(info: &core::panic::PanicInfo) -> ! {
  log::error!("PANIC: {info:#?}");
  loop {}
}
