// #Sireum

package base.runtimemonitor

import org.sireum._
import base._

// Do not edit this file as it will be overwritten if HAMR codegen is rerun

object GumboXDispatcher {
  def checkContract(observationKind: ObservationKind.Type, preContainer: Option[art.DataContent], postContainer: Option[art.DataContent]): B = {
    observationKind match {
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_producer_producer_postInit =>
        val result: B = base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_GumboX.inititialize_IEP_Post_Container(postContainer.get.asInstanceOf[base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_PostState_Container_PS])
        //println(s"producer.initialise: Post-condition: ${if (result) "" else "un"}satisfied")
        return result
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_producer_producer_preCompute =>
        // checking the pre-state values of producer's compute entrypoint is not required
        return T
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_producer_producer_postCompute =>
        val result: B = base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_GumboX.compute_CEP_Post_Container(preContainer.get.asInstanceOf[base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_PreState_Container_PS], postContainer.get.asInstanceOf[base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_PostState_Container_PS])
        //println(s"producer.timeTriggered: Post-condition: ${if (result) "" else "un"}satisfied")
        return result

      case base.runtimemonitor.ObservationKind.Sys_i_Instance_consumer_consumer_postInit =>
        // checking the post-state values of consumer's initialise entrypoint is not required
        return T
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_consumer_consumer_preCompute =>
        val result: B = base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_GumboX.compute_CEP_Pre_Container(preContainer.get.asInstanceOf[base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_PreState_Container_PS])
        //println(s"consumer.timeTriggered: Pre-condition: ${if (result) "" else "un"}satisfied")
        return result
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_consumer_consumer_postCompute =>
        val result: B = base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_GumboX.compute_CEP_Post_Container(preContainer.get.asInstanceOf[base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_PreState_Container_PS], postContainer.get.asInstanceOf[base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_PostState_Container_PS])
        //println(s"consumer.timeTriggered: Post-condition: ${if (result) "" else "un"}satisfied")
        return result

      case _ => halt("Infeasible")
    }
  }

  def genTestSuite(testCases: ISZ[(Z, ISZ[ST])]): Unit = {
    val tq = "\"\"\""

    val testRoot = Os.path(".") / "src" / "test" / "bridge"

    val ProducerThr_i_producer_producer_id = Arch.Sys_i_Instance_producer_producer.id
    val ConsumerThr_i_consumer_consumer_id = Arch.Sys_i_Instance_consumer_consumer.id

    def genUniqueSuiteName(path: Os.Path, prefix: String): String = {
      var i = 0
      while(true) {
        val cand = path / s"${prefix}_${i}.scala"
        if (!cand.exists) {
          return s"${prefix}_${i}"
        }
        i = i + 1
      }
      halt("Infeasible")
    }

    for (p <- testCases) {
      art.Art.BridgeId.fromZ(p._1) match {
        case ProducerThr_i_producer_producer_id =>
          val prefix = "ProducerThr_i_producer_producer_RM_TestSuite"
          val path = testRoot /+ ISZ("base","Gumbo_Structs_Arrays")
          val suiteName = genUniqueSuiteName(path, prefix)

          val testSuite =
            st"""package base.Gumbo_Structs_Arrays
                |
                |import org.sireum._
                |import base.Gumbo_Structs_Arrays._
                |
                |class ${suiteName} extends ProducerThr_i_producer_producer_GumboX_TestHarness_ScalaTest {
                |  val verbose: B = T
                |
                |  ${p._2}
                |}"""
          val filename = path / s"${suiteName}.scala"
          filename.writeOver(testSuite.render)
          println(s"Wrote: ${filename.toUri}")
        case ConsumerThr_i_consumer_consumer_id =>
          val prefix = "ConsumerThr_i_consumer_consumer_RM_TestSuite"
          val path = testRoot /+ ISZ("base","Gumbo_Structs_Arrays")
          val suiteName = genUniqueSuiteName(path, prefix)

          val testSuite =
            st"""package base.Gumbo_Structs_Arrays
                |
                |import org.sireum._
                |import base.Gumbo_Structs_Arrays._
                |
                |class ${suiteName} extends ConsumerThr_i_consumer_consumer_GumboX_TestHarness_ScalaTest {
                |  val verbose: B = T
                |
                |  ${p._2}
                |}"""
          val filename = path / s"${suiteName}.scala"
          filename.writeOver(testSuite.render)
          println(s"Wrote: ${filename.toUri}")
        case x => halt(s"Infeasible bridge id: $x")
      }
    }
  }

  def genTestCase(observationKind: ObservationKind.Type, preContainer: Option[String], postContainer: Option[String], testNameSuffix: Option[String]): ST = {
    val tq = "\"\"\""
    val suffix: String =
      if (testNameSuffix.nonEmpty) testNameSuffix.get
      else ""

    observationKind match {
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_producer_producer_postInit =>
        return (st"""// Begin test cases for Sys_i_Instance_producer_producer_postInit
                    |
                    |test(s"Sys_i_Instance_producer_producer_postInit: Check Post-condition$suffix") {
                    |  val postJson: String = st${tq}${postContainer.get}${tq}.render
                    |  val postContainer = base.JSON.toGumbo_Structs_ArraysProducerThr_i_producer_producer_PostState_Container_PS(postJson).left
                    |  assert(base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_GumboX.inititialize_IEP_Post_Container(postContainer))
                    |}
                    |// End test cases for Sys_i_Instance_producer_producer_postInit""")
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_producer_producer_postCompute =>
        return (st"""// Begin test cases for Sys_i_Instance_producer_producer_postCompute
                    |
                    |test(s"Sys_i_Instance_producer_producer_postCompute: Check Post-condition$suffix") {
                    |  val preJson: String = st${tq}${preContainer.get}${tq}.render
                    |  val postJson: String = st${tq}${postContainer.get}${tq}.render
                    |  val preContainer = base.JSON.toGumbo_Structs_ArraysProducerThr_i_producer_producer_PreState_Container_PS(preJson).left
                    |  val postContainer = base.JSON.toGumbo_Structs_ArraysProducerThr_i_producer_producer_PostState_Container_PS(postJson).left
                    |  if (verbose) {
                    |    println("Pre-State Values:")
                    |    println(s"  $$preContainer")
                    |    println("Post-State Values:")
                    |    println(s"  $$postContainer");
                    |  }
                    |  assert(base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_GumboX.compute_CEP_Post_Container(preContainer, postContainer))
                    |}
                    |// End test cases for Sys_i_Instance_producer_producer_postCompute""")
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_consumer_consumer_preCompute =>
        return (st"""// Begin test cases for Sys_i_Instance_consumer_consumer_preCompute
                    |
                    |test(s"Sys_i_Instance_consumer_consumer_preCompute: Check Pre-condition$suffix") {
                    |  val preJson: String = st${tq}${preContainer.get}${tq}.render
                    |  val preContainer = base.JSON.toGumbo_Structs_ArraysConsumerThr_i_consumer_consumer_PreState_Container_PS(preJson).left
                    |  if (verbose) {
                    |    println("Pre-State Values:")
                    |    println(s"  $$preContainer")
                    |  }
                    |  assert(base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_GumboX.compute_CEP_Pre_Container(preContainer))
                    |}
                    |// End test cases for Sys_i_Instance_consumer_consumer_preCompute""")
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_consumer_consumer_postCompute =>
        return (st"""// Begin test cases for Sys_i_Instance_consumer_consumer_postCompute
                    |
                    |test(s"Sys_i_Instance_consumer_consumer_postCompute: Check Pre-condition$suffix") {
                    |  val preJson: String = st${tq}${preContainer.get}${tq}.render
                    |  val preContainer = base.JSON.toGumbo_Structs_ArraysConsumerThr_i_consumer_consumer_PreState_Container_PS(preJson).left
                    |  if (verbose) {
                    |    println("Pre-State Values:")
                    |    println(s"  $$preContainer")
                    |  }
                    |  assert(base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_GumboX.compute_CEP_Pre_Container(preContainer))
                    |}
                    |
                    |test(s"Sys_i_Instance_consumer_consumer_postCompute: Check Post-condition$suffix") {
                    |  val preJson: String = st${tq}${preContainer.get}${tq}.render
                    |  val postJson: String = st${tq}${postContainer.get}${tq}.render
                    |  val preContainer = base.JSON.toGumbo_Structs_ArraysConsumerThr_i_consumer_consumer_PreState_Container_PS(preJson).left
                    |  val postContainer = base.JSON.toGumbo_Structs_ArraysConsumerThr_i_consumer_consumer_PostState_Container_PS(postJson).left
                    |  if (verbose) {
                    |    println("Pre-State Values:")
                    |    println(s"  $$preContainer")
                    |    println("Post-State Values:")
                    |    println(s"  $$postContainer");
                    |  }
                    |  assert(base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_GumboX.compute_CEP_Post_Container(preContainer, postContainer))
                    |}
                    |// End test cases for Sys_i_Instance_consumer_consumer_postCompute""")
      case _ => return st"// TODO ${observationKind}"
    }
  }

  def getUpdates(bridge_id: art.Art.BridgeId, observationKind: ObservationKind.Type, container: art.DataContent): Map[String, String] = {
    observationKind match {
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_producer_producer_postInit =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_PostState_Container_PS]
        if (postContainer.api_myStructArray.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_myStructArray" ~> postContainer.api_myStructArray.get.string
        }
        if (postContainer.api_MyArrayStruct.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_MyArrayStruct" ~> postContainer.api_MyArrayStruct.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_consumer_consumer_postInit =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_PostState_Container_PS]
        return updates
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_producer_producer_preCompute =>
        var updates: Map[String, String] = Map.empty
        val preContainer = container.asInstanceOf[base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_PreState_Container_PS]
        return updates
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_consumer_consumer_preCompute =>
        var updates: Map[String, String] = Map.empty
        val preContainer = container.asInstanceOf[base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_PreState_Container_PS]
        if (preContainer.api_myStructArray.nonEmpty) {
          updates = updates + s"${bridge_id}_In_myStructArray" ~> preContainer.api_myStructArray.get.string
        }
        if (preContainer.api_MyArrayStruct.nonEmpty) {
          updates = updates + s"${bridge_id}_In_MyArrayStruct" ~> preContainer.api_MyArrayStruct.get.string
        }
        if (preContainer.api_MyArrayInt32.nonEmpty) {
          updates = updates + s"${bridge_id}_In_MyArrayInt32" ~> preContainer.api_MyArrayInt32.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_producer_producer_postCompute =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.Gumbo_Structs_Arrays.ProducerThr_i_producer_producer_PostState_Container_PS]
        if (postContainer.api_myStructArray.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_myStructArray" ~> postContainer.api_myStructArray.get.string
        }
        if (postContainer.api_MyArrayStruct.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_MyArrayStruct" ~> postContainer.api_MyArrayStruct.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.Sys_i_Instance_consumer_consumer_postCompute =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.Gumbo_Structs_Arrays.ConsumerThr_i_consumer_consumer_PostState_Container_PS]
        return updates
      case _ => return Map.empty
    }
  }
}