// #Sireum

package firewall.SW

import org.sireum._
import art.Art
import firewall._

// Do not edit this file as it will be overwritten if HAMR codegen is rerun
@msig trait ArduPilot_Impl_ArduPilot_ArduPilot_TestApi {

  def BeforeEntrypoint(): Unit = {
    Art.initTest(Arch.seL4_Impl_Instance_ArduPilot_ArduPilot)
  }

  def AfterEntrypoint(): Unit = {
    Art.finalizeTest(Arch.seL4_Impl_Instance_ArduPilot_ArduPilot)
  }

  def testCompute(): Unit = {
    Art.manuallyClearOutput()
    Art.testCompute(Arch.seL4_Impl_Instance_ArduPilot_ArduPilot)
  }

  def testInitialise(): Unit = {
    Art.manuallyClearOutput()
    Art.testInitialise(Arch.seL4_Impl_Instance_ArduPilot_ArduPilot)
  }

  /** helper function to set the values of all input ports.
   * @param EthernetFramesRx payloads for event data port EthernetFramesRx.
   *   ART currently supports single element event data queues so
   *   only the last element of EthernetFramesRx will be used
   */
  def put_concrete_inputs(EthernetFramesRx : ISZ[SW.RawEthernetMessage]): Unit = {
    for(v <- EthernetFramesRx){
      put_EthernetFramesRx(v)
    }
  }


  /** helper function to check ArduPilot_Impl_ArduPilot_ArduPilot's
   * output ports.  Use named arguments to check subsets of the output ports.
   * @param EthernetFramesTx method that will be called with the payloads to be sent
   *        on the outgoing event data port 'EthernetFramesTx'.
   */
  def check_concrete_output(EthernetFramesTx: ISZ[SW.RawEthernetMessage] => B): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    var EthernetFramesTxValue: ISZ[SW.RawEthernetMessage] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_EthernetFramesTx().nonEmpty) { EthernetFramesTxValue = EthernetFramesTxValue :+ get_EthernetFramesTx().get }
    if(!EthernetFramesTx(EthernetFramesTxValue)) {
      testFailures = testFailures :+ st"'EthernetFramesTx' did not match expected: received ${EthernetFramesTxValue.size} events with the following payloads ${EthernetFramesTxValue}"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // setter for in EventDataPort
  def put_EthernetFramesRx(value : SW.RawEthernetMessage): Unit = {
    Art.insertInInfrastructurePort(Arch.seL4_Impl_Instance_ArduPilot_ArduPilot.operational_api.EthernetFramesRx_Id, SW.RawEthernetMessage_Payload(value))
  }

  // getter for out EventDataPort
  def get_EthernetFramesTx(): Option[SW.RawEthernetMessage] = {
    val value: Option[SW.RawEthernetMessage] = get_EthernetFramesTx_payload() match {
      case Some(SW.RawEthernetMessage_Payload(v)) => Some(v)
      case Some(v) => halt(s"Unexpected payload on port EthernetFramesTx.  Expecting 'SW.RawEthernetMessage_Payload' but received ${v}")
      case _ => None[SW.RawEthernetMessage]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_EthernetFramesTx_payload(): Option[SW.RawEthernetMessage_Payload] = {
    return Art.observeOutInfrastructurePort(Arch.seL4_Impl_Instance_ArduPilot_ArduPilot.initialization_api.EthernetFramesTx_Id).asInstanceOf[Option[SW.RawEthernetMessage_Payload]]
  }

}
