// #Sireum

package firewall.SW

import org.sireum._
import art.Art
import firewall._

// Do not edit this file as it will be overwritten if HAMR codegen is rerun
@msig trait LowLevelEthernetDriver_Impl_LowLevelEthernetDriver_LowLevelEthernetDriver_TestApi {

  def BeforeEntrypoint(): Unit = {
    Art.initTest(Arch.seL4_Impl_Instance_LowLevelEthernetDriver_LowLevelEthernetDriver)
  }

  def AfterEntrypoint(): Unit = {
    Art.finalizeTest(Arch.seL4_Impl_Instance_LowLevelEthernetDriver_LowLevelEthernetDriver)
  }

  def testCompute(): Unit = {
    Art.manuallyClearOutput()
    Art.testCompute(Arch.seL4_Impl_Instance_LowLevelEthernetDriver_LowLevelEthernetDriver)
  }

  def testInitialise(): Unit = {
    Art.manuallyClearOutput()
    Art.testInitialise(Arch.seL4_Impl_Instance_LowLevelEthernetDriver_LowLevelEthernetDriver)
  }

  /** helper function to set the values of all input ports.
   * @param EthernetFramesTx payloads for event data port EthernetFramesTx.
   *   ART currently supports single element event data queues so
   *   only the last element of EthernetFramesTx will be used
   */
  def put_concrete_inputs(EthernetFramesTx : ISZ[SW.RawEthernetMessage]): Unit = {
    for(v <- EthernetFramesTx){
      put_EthernetFramesTx(v)
    }
  }


  /** helper function to check LowLevelEthernetDriver_Impl_LowLevelEthernetDriver_LowLevelEthernetDriver's
   * output ports.  Use named arguments to check subsets of the output ports.
   * @param EthernetFramesRx method that will be called with the payloads to be sent
   *        on the outgoing event data port 'EthernetFramesRx'.
   */
  def check_concrete_output(EthernetFramesRx: ISZ[SW.RawEthernetMessage] => B): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    var EthernetFramesRxValue: ISZ[SW.RawEthernetMessage] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_EthernetFramesRx().nonEmpty) { EthernetFramesRxValue = EthernetFramesRxValue :+ get_EthernetFramesRx().get }
    if(!EthernetFramesRx(EthernetFramesRxValue)) {
      testFailures = testFailures :+ st"'EthernetFramesRx' did not match expected: received ${EthernetFramesRxValue.size} events with the following payloads ${EthernetFramesRxValue}"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // setter for in EventDataPort
  def put_EthernetFramesTx(value : SW.RawEthernetMessage): Unit = {
    Art.insertInInfrastructurePort(Arch.seL4_Impl_Instance_LowLevelEthernetDriver_LowLevelEthernetDriver.operational_api.EthernetFramesTx_Id, SW.RawEthernetMessage_Payload(value))
  }

  // getter for out EventDataPort
  def get_EthernetFramesRx(): Option[SW.RawEthernetMessage] = {
    val value: Option[SW.RawEthernetMessage] = get_EthernetFramesRx_payload() match {
      case Some(SW.RawEthernetMessage_Payload(v)) => Some(v)
      case Some(v) => halt(s"Unexpected payload on port EthernetFramesRx.  Expecting 'SW.RawEthernetMessage_Payload' but received ${v}")
      case _ => None[SW.RawEthernetMessage]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_EthernetFramesRx_payload(): Option[SW.RawEthernetMessage_Payload] = {
    return Art.observeOutInfrastructurePort(Arch.seL4_Impl_Instance_LowLevelEthernetDriver_LowLevelEthernetDriver.initialization_api.EthernetFramesRx_Id).asInstanceOf[Option[SW.RawEthernetMessage_Payload]]
  }

}
