//! C-interface for the component.
//! This code must be unsafe.
//! Assumptions about correctness are introduced and need to be verified by other means.

use data::*;

#[cfg(test)]
use std::sync::Mutex;

#[cfg(not(test))]
extern "C" {
  fn get_EthernetFramesTx(value: *mut SW::RawEthernetMessage) -> bool;
  fn put_EthernetFramesRx(value: *mut SW::RawEthernetMessage) -> bool;
}

pub fn unsafe_get_EthernetFramesTx() -> Option<SW::RawEthernetMessage> 
 {
   unsafe {
     let value: *mut SW::RawEthernetMessage = &mut [0; SW::SW_RawEthernetMessage_DIM_0];
     if (get_EthernetFramesTx(value)) {
       return Some(*value);
     } else {
       return None;
     }
   }
 }

pub fn unsafe_put_EthernetFramesRx(value: &SW::RawEthernetMessage) -> bool 
 {
   unsafe {
     return put_EthernetFramesRx(value as *const SW::RawEthernetMessage as *mut SW::RawEthernetMessage);
   }
 }

//////////////////////////////////////////////////////////////////////////////////
// Testing Versions
//////////////////////////////////////////////////////////////////////////////////

#[cfg(test)]
lazy_static::lazy_static! {
  // simulate the global C variables that point to the microkit shared memory regions.  In a full
  // microkit system we would be able to mutate the shared memory for out ports since they're r/w,
  // but we couldn't do that for in ports since they are read-only
  pub static ref IN_EthernetFramesTx: Mutex<Option<SW::RawEthernetMessage>> = Mutex::new(None);
  pub static ref OUT_EthernetFramesRx: Mutex<Option<SW::RawEthernetMessage>> = Mutex::new(None);
}

#[cfg(test)]
pub fn initialize_test_globals() {
  unsafe {
    *IN_EthernetFramesTx.lock().unwrap() = None;
    *OUT_EthernetFramesRx.lock().unwrap() = None;
  }
}

#[cfg(test)]
pub fn get_EthernetFramesTx(value: *mut SW::RawEthernetMessage) -> bool 
 {
   unsafe {
     match *IN_EthernetFramesTx.lock().unwrap() {
       Some(v) => {
         *value = v;
         return true;
       },
       None => return false,
     }
   }
 }

#[cfg(test)]
pub fn put_EthernetFramesRx(value: *mut SW::RawEthernetMessage) -> bool 
 {
   unsafe {
     *OUT_EthernetFramesRx.lock().unwrap() = Some(*value);
     return true;
   }
 }
