#[cfg(test)]
lazy_static::lazy_static! {
  // simulate the global C variables that point to the microkit shared memory regions.  In a full
  // microkit system we would be able to mutate the shared memory for out ports since they're r/w,
  // but we couldn't do that for in ports since they are read-only
  pub static ref IN_EthernetFramesTxIn: Mutex<Option<SW::StructuredEthernetMessage_i>> = Mutex::new(None);
  pub static ref OUT_EthernetFramesRxOut: Mutex<Option<SW::StructuredEthernetMessage_i>> = Mutex::new(None);
  pub static ref OUT_EthernetFramesTxOut: Mutex<Option<SW::StructuredEthernetMessage_i>> = Mutex::new(None);
  pub static ref IN_EthernetFramesRxIn: Mutex<Option<SW::StructuredEthernetMessage_i>> = Mutex::new(None);
}

#[cfg(test)]
pub fn initialize_test_globals() {
  unsafe {
    *IN_EthernetFramesTxIn.lock().unwrap() = None;
    *OUT_EthernetFramesRxOut.lock().unwrap() = None;
    *OUT_EthernetFramesTxOut.lock().unwrap() = None;
    *IN_EthernetFramesRxIn.lock().unwrap() = None;
  }
}

#[cfg(test)]
pub fn get_EthernetFramesTxIn(value: *mut SW::StructuredEthernetMessage_i) -> bool 
 {
   unsafe {
     match *IN_EthernetFramesTxIn.lock().unwrap() {
       Some(v) => {
         *value = v;
         return true;
       },
       None => return false,
     }
   }
 }

